const BASE_URLS = {
    hello: "http://localhost:8080",
    query: "http://localhost:9000",
    count: "http://localhost:3333"
};

export async function getHello() {
    const response = await fetch(`${BASE_URLS.hello}/get`);
    return response.text();
}

export async function getUser(name) {
    const response = await fetch(`${BASE_URLS.query}/api/user?name=${name}`);
    return response.text();
}

export async function getCount() {
    const response = await fetch(`${BASE_URLS.count}/count`);
    return response.text();
}

export async function postCount(value) {
    const response = await fetch(`${BASE_URLS.count}/count`, {
        method: "POST",
        headers: { "Content-Type": "application/x-www-form-urlencoded" },
        body: `count=${value}`
    });
    if (!response.ok) {
        throw new Error("Ошибка обновления счётчика");
    }
    return response.text();
}
